import React, { Component } from 'react';
import Snake from './Snake';
import Food from './Food';



const getRandomCordinates = () => {
      let min = 1 ;
      let max = 98 ;
      let x = Math.floor(min + (max - min) * Math.random());
      let y = Math.floor(min + (max - min) * Math.random());

      return [x,y]
  }

const initialState  = {
     speed:150,
     food: getRandomCordinates(),
     direction:'RIGHT',
     snakeDots: [
      [0,0],
      [2,0]
     ]
}  

class App extends Component {


  state = initialState;

  componentDidMount() {
    setInterval(this.moveSnake,this.state.speed);
    document.onkeydown = this.onKeyDown;
  }

  componentDidUpdate(){
    this.snakeCollapsed();
    this.checkBorder();
  }

  onKeyDown = (e) => {
    e=e ||window.event;
    switch(e.keyCode){
      case 37:
      this.setState({direction:'LEFT'});
      break;
      case 38:
      this.setState({direction:'TOP'});
      break;
      case 39:
      this.setState({direction:'RIGHT'});
      break;
      case 40:
      this.setState({direction:'DOWN'});
      break;

    }
  }

  moveSnake = () => {
    let dots = [...this.state.snakeDots];
    let head = dots[dots.length - 1];

    switch(this.state.direction){
      case 'RIGHT':
      head = [head[0]+2,head[1]];
      break;
      case 'LEFT':
      head = [head[0]-2,head[1]];
      break;
      case 'TOP':
      head = [head[0],head[1]-2];
      break;
      case 'DOWN':
      head = [head[0],head[1]+2];
      break;
    }
    dots.push(head);
    dots.shift();
    this.setState({
      snakeDots:dots
    })

  }

  checkBorder(){
    let border = this.state.snakeDots[this.state.snakeDots.length - 1];
    if(border[0]>100|| border[1]>100 || border[0]< 0 || border[1] < 0){
      this.gameOver();
    }

  }

  gameOver(){
    alert("game over", this.state.snakeDots.length-1);
    this.setState(initialState);

  }

  snakeCollapsed(){
    let snake = [...this.state.snakeDots];
    let head  = snake[snake.length - 1];
    snake.pop();
    snake.forEach(dots =>{
        if(dots[0] == head[0] && dots[1] == head[1])
        {
          this.gameOver();
        }
    })
  }

  snakeEatingFood(){
    let dots = [...this.state.snakeDots];
    let head = dots[dots.length - 1];
    let food =  this.state.food;
    if(food[0] = head[0] && food[1] == snake[1]){
      this.increaseSize();
    }

  }

  increaseSize(){
    let newSnake = [...this.state.snakeDots];
    newSnake.unshift([]);
    let Speed = this.state.speed;
    Speed = Speed +50;

    this.setState({
      snakeDots:newSnake,
      speed:Speed
    })
  }

  render() {
        return(
          <div className="game-area">
            <Snake snakeDots={this.state.snakeDots} />
            <Food dot={this.state.food} />
          </div> 
      );
  }
}


export default App;
